// book-list.component.ts

import { Component, OnInit } from '@angular/core';
import { BookService } from '../services/book.service';
import { Book } from '../models/book.model';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit {
  books: Book[] = []; 

  constructor(private bookService: BookService) {}

  ngOnInit() {
    this.getBooks();
  }

  getBooks(): void {
    this.bookService.getBooks()
      .subscribe(
        books => this.books = books,
        error => console.error('Erro ao obter livros:', error)
      );
  }

  deleteBook(id: number): void {
    this.bookService.deleteBook(id)
      .subscribe(
        () => {
          this.books = this.books.filter(book => book.id !== id);
          console.log('Livro excluído com sucesso!');
        },
        error => console.error('Erro ao excluir livro:', error)
      );
  }
}
<h2>Book List</h2>

<table class="table">
  <thead class="thead-dark">
    <tr>
      <th scope="col">Nome</th>
      <th scope="col">Categoria</th>
      <th scope="col">Valor</th>
      <th scope="col">Quantidade</th>
      <th scope="col">Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let book of books">
      <td>{{ book.nome }}</td>
      <td>{{ book.categoria }}</td>
      <td>{{ book.valor }}</td>
      <td>{{ book.quantidadeEstoque }}</td>
      <td>
        <a class="btn btn-primary" [routerLink]="['/books', book.id]">View</a>
        <a class="btn btn-success" [routerLink]="['/books', book.id, 'edit']">Edit</a>
        <a class="btn btn-danger" (click)="deleteBook(book.id)">Delete Book</a>
      </td>
    </tr>
  </tbody>
</table>

<a class="btn btn-primary" [routerLink]="'/books/create'">Create book</a>




<app-weather></app-weather>
