import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Book } from '../models/book.model';
import { BookService } from '../services/book.service';

@Component({
  selector: 'app-book-details',
  templateUrl: './book-details.component.html',
  styleUrls: ['./book-details.component.css']
})
export class BookDetailsComponent implements OnInit {
  book: Book | undefined;

  constructor(
    private bookService: BookService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const id = +params.get('id')!;
      this.getBook(id);
    });
  }

  getBook(id: number): void {
    this.bookService.getBook(id)
      .subscribe(book => {
        this.book = book;
      });
  }
}
<h2>Book Details</h2>

<div *ngIf="book">
  <div><strong>Title:</strong> {{ book.nome }}</div>
  <div><strong>Category:</strong> {{ book.categoria }}</div>
  <div><strong>Value:</strong> {{ book.valor | currency:'BRL' }}</div>
  <div><strong>Stock Quantity:</strong> {{ book.quantidadeEstoque }}</div>
</div>

<a [routerLink]="'/books'">Back to List</a>
