import { Component, OnInit } from '@angular/core';
import { BookService } from '../services/book.service';
import { Book } from '../models/book.model';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit {
  books: Book[] = []; // Inicialize o array de livros

  constructor(private bookService: BookService) {}

  ngOnInit() {
    this.getBooks();
  }

  getBooks(): void {
    this.bookService.getBooks()
      .subscribe(books => this.books = books); // Atribui a lista de livros recebida do serviço
  }

  deleteBook(id: number): void {
    this.bookService.deleteBook(id)
      .subscribe(() => {
        this.books = this.books.filter(book => book.id !== id); // Remove o livro da lista local após a exclusão
      });
  }
}
